CREATE TABLE `entity_photos` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `entity_id` bigint(20) NOT NULL COMMENT 'ссылка на PK таблицы отеля, курорта, страны и т.д.',
  `proto_name` varchar(255) NOT NULL,
  `guid` varchar(50) NOT NULL COMMENT 'имя файла (guid) без разширения',
  `original` varchar(50) NOT NULL COMMENT 'оригинальное изображение',
  `thumbnail_small` varchar(50) NOT NULL COMMENT 'уменьшенное изображение (140x96 px) для отображения в списках',
  `thumbnail_mid` varchar(50) NOT NULL COMMENT 'среднее изображение (220x146 px) для отображения в галерее',
  `thumbnail_large` varchar(50) NOT NULL COMMENT 'большое изображение (620x424 px) для отображения главного изображения в галерее',
  `is_main` bit(1) DEFAULT b'0' COMMENT 'использовать как главную (первую) фотографию в галерее',
  `order_number` int(11) NOT NULL DEFAULT '1' COMMENT 'порядок следования фотографий в галерее',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=95 DEFAULT CHARSET=utf8 COMMENT='фотографии сущностей для галереи';
