INSERT INTO `pages` VALUES (1,NULL,'О курортах','2013-11-28 09:00:00','<p>\r\n	<a href=\"/resorts/spa\" target=\"\"><img alt=\"\" data-gallery=\"false\" src=\"/content/userdata/images/3a929ea8-d53c-43ee-896e-76f1729690cc.JPG\" style=\"width: 300px; height: 200px;\" /></a></p>\r\n<h2>\r\n	<a href=\"/resorts/spa\">Спа</a></h2>\r\n<div class=\"media_embed\">\r\n	<div class=\"media_embed\">\r\n		<iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"//www.youtube.com/embed/oNB5BpGHYUo?rel=0\" width=\"560\"></iframe></div>\r\n</div>\r\n<p>\r\n	<a href=\"/resorts/velnes\">Велнес</a></p>\r\n<p>\r\n	<a href=\"/resorts/types\">Какие бывают курорты</a></p>\r\n<p>\r\n	<a href=\"/resorts/home\">Курорт у вас дома</a></p>\r\n<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width: 500px;\" summary=\"аоеао\">\r\n	<caption>\r\n		полнлогн</caption>\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				Хочу велосипед</td>\r\n			<td>\r\n				Зеленый</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				фыва</td>\r\n			<td>\r\n				пассатижи</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				фыва</td>\r\n			<td>\r\n				вафыва</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<div class=\"media_embed\">\r\n	&nbsp;</div>\r\n<p>\r\n	&nbsp;</p>\r\n','resorts'),(2,NULL,'Welcome to Russia','2013-11-28 09:00:00','Раздел \"Welcome to Russia\"<br><a href=\"/welcometorussia/resorts\">Курорты России</a><br>','welcometorussia'),(3,NULL,'Советы туристам','2013-11-28 09:00:00','Раздел \"Советы туристам\"<br><a href=\"/tourists/luggage\">Что взять с собой в отпуск?</a><br>','tourists'),(4,NULL,'Библиотека','2013-12-04 09:00:00','Раздел библиотека<br><a href=\"/dictionary\">Курортнй словарь </a>','library'),(5,NULL,'О компании','2013-12-13 09:00:00','<p>\r\n	BeWell Medispa &amp; Wellness &ndash; &nbsp;организатор индивидуальных путешествий за здоровьем.<br />\r\n	Мы представляем &nbsp;абсолютно новый туристский продукт &ndash; персональный подбор оздоровительного отдыха по рекомендации врача, на основании индивидуальных особенностей и потребностей клиента с гарантией высокой результативности лечения.</p>\r\n<p>\r\n	Попова Татьяна Владимировна, Генеральный директор Компани BeWell:</p>\r\n<p>\r\n	<img alt=\"\" data-gallery=\"false\" src=\"/content/userdata/images/eb42dda6-a2ed-43d6-85e3-29ab9c213cd3.jpg\" style=\"width: 150px; float: left; height: 225px; border-width: 1px; border-style: solid; margin: 5px;\" /></p>\r\n<p>\r\n	<em>&laquo;На протяжении многих лет мы &nbsp;занимаемся туризмом и регулярно сталкиваемся с необходимостью организации поездок наших клиентов с целью оздоровления.&nbsp; Текущая ситуация, сложившаяся&nbsp; на современном рынке реализации санаторно-курортных услуг такова, что при наличии широкого ассортимента предложений здравниц и курортов&nbsp; вероятность выбора&nbsp; потребителем эффективного оздоровления труднодостижима из-за отсутствия профессиональной&nbsp; консультации. Медицинские специалисты не просвещены относительно современного состояния санаторно-курортных услуг, в то время как менеджеры турфирм, зная географические и инфраструктурные особенности курортов, не обладают достаточной квалификацией, чтобы подобрать необходимые конкретному туристу программы лечения, и как следствие курорт и/или санаторий. Созданный нами проект, объединяющей в себе консультационно-диагностические услуги медицинских специалистов и знания туристской географии поможет решить проблему туриста и обеспечит индивидуальный подбор лечебных методик для каждого клиента в частности &raquo;</em></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Целью проекта BeWell &nbsp;является&nbsp; индивидуальный подбор оздоровительного тура на основании собранного анамнеза и диагностических исследований.&nbsp; Наша главная задача &ndash; помочь потенциальному клиенту подобрать результативную программу оздоровления.</p>\r\n<p>\r\n	Система подбора санаторно-курортного лечения, разработанная нами, обеспечивает индивидуальный подбор курортов, учитывая комплексный диагноз пациента, включающий наряду с основным сопутствующие, фоновые и сочетанные заболевания.&nbsp;</p>\r\n','about'),(6,NULL,'Контакты','2013-12-13 09:00:00','Тут будут контакты','contacts'),(7,NULL,'Карта проезда','2013-12-13 09:00:00','Тут будет карта проезда','map'),(8,1,'Спа','2013-11-28 09:00:00','Тут будет информация о спа','spa'),(9,1,'Велнес','2013-11-28 09:00:00','Тут будет информация о велнес','velnes'),(10,1,'Какие бывают курорты','2013-11-28 09:00:00','Перечисление типов курортов','types'),(11,1,'Курорт у вас дома','2013-11-28 09:00:00','<a href=\"/resorts/home/recipes\">Коллекция рецептов здоровья от BeWell-Travel</a><br>\r\n<a href=\"/resorts/home/spa\">Спа салон у вас дома</a><br>\r\n','home'),(12,2,'Курорты России','2013-11-28 09:00:00','Страница раздела \"Welcome to Russia\"','resorts'),(13,3,'Что взять с собой в отпуск?','2013-11-28 09:00:00','Страница раздела \"Советы туристам\"','luggage'),(14,11,'Коллекция рецептов здоровья от BeWell-Travel','2013-11-28 09:00:00','<a href=\"/resorts/home/recipes/meal\">Здорового питания</a><br><a href=\"/resorts/home/recipes/weight\">Снижения веса</a><br><a href=\"/resorts/home/recipes/cosmetics\">Рецепты домашней косметики</a><br>','recipes'),(15,14,'Здорового питания','2013-11-28 09:00:00','Страница о здоровом питании','meal'),(16,14,'Снижения веса','2013-11-28 09:00:00','Страница о снижении веса','weight'),(17,14,'Рецепты домашней косметики','2013-11-28 09:00:00','Страница с рецептами косметики','cosmetics'),(18,11,'Спа салон у вас дома','2013-11-28 09:00:00','<a href=\"/resorts/home/spa/bowels\">Очищение кишечника в домашних условиях</a><br>','spa'),(19,18,'Очищение кишечника в домашних условиях','2013-11-28 09:00:00','Страница об очищении кишечника','bowels'),(20,NULL,'Туризм для всех','2014-11-01 09:00:00','Тут будет раздел \"Туризм для всех\"','tourism'),(21,0,'Партенит','2014-01-20 00:00:00','<p>\r\n	Здесь будет текст&nbsp;</p>\r\n','partenit');
